package labs;

public class ComputeArea {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double radius;
		double area;
		
		//Assign Radius
		radius = 20;
		
		//Compute Area
		area = radius * radius * 3.14159;
		
		System.out.println("The area of a circle with radius " + radius + " is " + area + ".");
		
		//Assign Radius
		radius = 10;
		
		//Compute Area
		area = radius * radius * 3.14159;
		
		System.out.println("The area of a circle with radius " + radius + " is " + area + ".");
		
		
	}

}
